CREATE TABLE IF NOT EXISTS users(userID integer NOT NULL PRIMARY KEY ON CONFLICT IGNORE, username text(15) NOT NULL, password text(1158) NOT NULL, role varchar(15)  NOT NULL, authenticated integer NOT NULL, expirationDate text, enabled integer  NOT NULL, isadmin integer  NOT NULL, email varchar(50), mobileNumber varchar(15)) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS buses(busID integer PRIMARY KEY ON CONFLICT IGNORE, busRegPlate varchar(15), assignedLine integer, assignedDriverID integer, busCondition int) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS stations(stationID integer PRIMARY KEY ON CONFLICT IGNORE, stationName text, stationAddress text, stationCoordinateX float, stationCoordinateY float, lineIDs text, stationState, integer) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS lines(lineID integer PRIMARY KEY ON CONFLICT IGNORE, lineStatus integer, stationIDs text, busIDs text) WITHOUT ROWID;


